window.addEventListener('resize', function () {
  const windowWidth = window.innerWidth;

  if (windowWidth >= 1200) {
    VANTA.NET({
      el: '#animated-body', // element selector string or DOM object reference
      mouseControls: false,
      touchControls: false,
      gyroControls: false,
      points: 20,
      maxDistance: 23,
      spacing: 20.0,
      scale: 1.0,
      scaleMobile: 1.0,
      color: 0x3385fa,
      backgroundColor: 0x092695,
    });
  } else if (windowWidth >= 768) {
    VANTA.NET({
      el: '#animated-body', // element selector string or DOM object reference
      mouseControls: false,
      touchControls: false,
      gyroControls: false,
      points: 9,
      maxDistance: 16,
      spacing: 15.0,
      scale: 1.0,
      scaleMobile: 1.0,
      color: 0x3385fa,
      backgroundColor: 0x092695,
    });
  } else if (windowWidth >= 320) {
    VANTA.NET({
      el: '#animated-body', // element selector string or DOM object reference
      mouseControls: false,
      touchControls: false,
      gyroControls: false,
      points: 5,
      maxDistance: 10,
      spacing: 10.0,
      scale: 1.0,
      scaleMobile: 1.0,
      color: 0x3385fa,
      backgroundColor: 0x092695,
    });
  }
});
