let vantaEffect = null;

function initVanta() {
  const container = document.getElementById('animated-body');

  vantaEffect = VANTA.NET({
    el: container,
    mouseControls: false,
    touchControls: false,
    gyroControls: false,
    points: 20,
    maxDistance: 22,
    spacing: 20.0,
    scale: 1.0,
    scaleMobile: 1.0,
    color: 0x3385fa,
    backgroundColor: 0x092695,
  });
}
document.addEventListener('DOMContentLoaded', () => {
  initVanta();

  // Слідкуємо за змінами розмірів вікна
  window.addEventListener('resize', () => {
    // При зміні розмірів оновлюємо Vanta.js
    if (vantaEffect) {
      vantaEffect.resize();
    }
  });
});
